@js:
function urlencode(str,charset){
	  return Array.from(str.split('&')).map(v=>{
	  	  vindex=v.indexOf('=')
	     param1=v.substring(0,vindex)
	     param2=v.substring(vindex+1,v.length)
	     param2=java.encodeURI(param2,charset)
	     return String(`${param1}=${param2}`)
	  }).join('&')
}
function randomStr(){
	  resultstr=String("")
	  times=(Math.random()*6+15).toFixed(0)
	  function randomchar(){
	  	  chars='1234567890bcdefghijklmnopqrstuvwxyz'
      random=Math.random()
      cindex=Math.floor(random*chars.length)
      return chars.charAt(cindex)
	  }
	  while(resultstr.length<times){
	  	  resultstr=String(`${resultstr}${randomchar()}`)
	  }
	  return resultstr
}
if(source.getVariable()==null||source.getVariable()==''){
	  username=randomStr()
	  password=randomStr()
	  email=`${randomStr()}@163.com`
	  java.log(`🍑用户名：${username}`)
	  java.log(`🍑密码：${password}`)
	  java.log(`🍑邮箱：${email}`)
	  submit='提 交 注 册'
	  rbody=urlencode(`username=${username}&password=${password}&repassword=${password}&email=${email}&action=newuser&submit=${submit}`,'gbk')
	  rurl='https://www.23qb.com/register.php?do=submit'
	  rep=java.ajax(`${rurl},${JSON.stringify({
	  	  "charset":"gbk",
	  	  "method":"post",
	  	  "body":rbody,
	  	  "headers":{
	  	  	  "Content-Type":"application/x-www-form-urlencoded"
	  	  }
	  })}`)
	  java.log(rep.match(/注册成功/g)[0])
	  if(rep.indexOf('注册成功')>-1){
	  	  java.toast('自动注册成功')
	  	  source.setVariable(JSON.stringify({
	  	  	  "name":username,
	  	  	  "pass":password,
	  	  	  "email":email,
	  	  }))
	  }else{
	  	  java.log(`❗️出错\n${rep}`)
	  }
}
loginInfo=JSON.parse(source.getVariable())
lurl='https://www.23qb.com/login.php?do=submit'
lbody=urlencode(`username=${loginInfo.name}&password=${loginInfo.pass}&usecookie=2592000&action=login&submit=%26%23160%3B%B5%C7%26%23160%3B%26%23160%3B%C2%BC%26%23160%3B`,'gbk')
rep=java.ajax(`${lurl},${JSON.stringify({
